refsect2.programlisting:
typedef struct {
  PangoAttrType type;
  PangoAttribute * (*copy) (const PangoAttribute *attr);
  void             (*destroy) (PangoAttribute *attr);
  gboolean         (*equal) (const PangoAttribute *attr1, const PangoAttribute *attr2);
} PangoAttrClass;

typedef struct {
  const PangoAttrClass *klass;
  guint start_index;	/* in bytes */
  guint end_index;	/* in bytes. The character at this index is not included */
} PangoAttribute;

typedef struct {
  PangoAttribute attr;
  char *value;
} PangoAttrString;

typedef struct {
  PangoAttribute attr;
  PangoLanguage *value;
} PangoAttrLanguage;

typedef struct {
  PangoAttribute attr;
  PangoColor color;
} PangoAttrColor;

typedef struct {
  PangoAttribute attr;
  int value;
} PangoAttrInt;

typedef struct {
  PangoAttribute attr;
  double value;
} PangoAttrFloat;

typedef struct {
  PangoAttribute attr;
  PangoFontDescription *desc;
} PangoAttrFontDesc;

typedef struct {
  PangoAttribute attr;
  PangoRectangle ink_rect;
  PangoRectangle logical_rect;

  gpointer              data;
  PangoAttrDataCopyFunc copy_func;
  GDestroyNotify        destroy_func;
} PangoAttrShape;

typedef struct {
  PangoAttribute attr;
  int size;
  guint absolute : 1;
} PangoAttrSize;

PangoAttribute*     pango_attr_foreground_new           (guint16 red,
                                                         guint16 green,
                                                         guint16 blue);

PangoAttribute*     pango_attr_background_new           (guint16 red,
